// Select best topic for US audience
const topics = $input.all();
const filtered = topics.filter(t => 
  t.json.viewCount > 50000 && 
  t.json.engagement > 0.05
);

const selected = filtered[0];
return [{
  json: {
    topic: selected.json.title,
    keywords: selected.json.tags,
    targetAudience: 'US adults 18-45',
    tone: 'conversational and friendly'
  }
}];

// Extract and validate script
const response = $json.candidates[0].content.parts[0].text;
const wordCount = response.split(/\s+/).length;

// Calculate estimated video length
const speakingRate = 155; // words per minute for natural speech
const estimatedMinutes = wordCount / speakingRate;

if (estimatedMinutes < 10 || estimatedMinutes > 12) {
  throw new Error(`Script length ${estimatedMinutes.toFixed(1)} min is outside target 10-12 min range`);
}

return [{
  json: {
    script: response,
    wordCount: wordCount,
    estimatedDuration: estimatedMinutes,
    topic: $node["Select Topic for US Audience"].json.topic,
    targetLength: '10-12 minutes'
  }
}];

// Download video files from Pexels
const videos = $json.videos.slice(0, 10);
const downloads = [];

for (let i = 0; i < videos.length; i++) {
  const video = videos[i];
  const url = video.video_files.find(f => f.quality === 'hd').link;
  downloads.push({
    url: url,
    filename: `clip_${i}.mp4`,
    duration: video.duration
  });
}

return downloads.map(d => ({ json: d }));

